mapper.select:
SELECT COUNT(*) FROM task
WHERE 1=1

    AND task_name LIKE CONCAT('%', #{taskName}, '%')


    AND status = #{taskStatus}





            AND station in ('九堡', '彭埠', '九堡彭埠')



            AND station = #{station}

SELECT * FROM task
WHERE 1=1

    AND task_name LIKE CONCAT('%', #{taskName}, '%')


    AND status = #{taskStatus}





            AND station in ('九堡', '彭埠', '九堡彭埠')



            AND station = #{station}



ORDER BY created_time,task_id DESC
LIMIT #{offset}, #{pageSize}
